  }
}

///
///

enum ZwpTextInputV1TextDirection {
  /// automatic text direction based on text and language
  auto("auto", 0),

  /// left-to-right
  ltr("ltr", 1),

  /// right-to-left
  rtl("rtl", 2);

  const ZwpTextInputV1TextDirection(this.enumName, this.enumValue);
